INSERT INTO `wp_posts` VALUES (55,1,'2024-10-23 12:38:19','2024-10-23 10:38:19','<!-- wp:heading {\"textAlign\":\"center\",\"level\":1} -->\n<h1 class=\"wp-block-heading has-text-align-center\">A propos</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><strong>Nathalie Mota</strong> est une photographe freelance passionne par lart de capturer des moments uniques. Avec plusieurs annes dexprience dans la photographie dvnements, de portraits et de paysages, Nathalie propose des services sur mesure adapts  vos besoins, que ce soit pour immortaliser un mariage, un concert, une rception prive ou une sance photo professionnelle.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Son approche crative et attentionne permet de capturer lessence de chaque moment, avec un souci du dtail et un il artistique affin.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Services proposs</strong> :</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> Photographie dvnements (mariages, rceptions, concerts, etc.)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> Portraits individuels et de groupe</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> Photos pour projets professionnels et artistiques</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour toute demande de renseignements ou de collaboration, nhsitez pas  <strong>me contacter</strong> via le formulaire disponible sur le site.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','A propos','','publish','closed','closed','','a-propos','','','2024-10-23 12:38:19','2024-10-23 10:38:19','',0,'http://localhost:10034/?page_id=55',0,'page','',0);
